import React from 'react'
import {Link} from 'react-router-dom'
import { connect } from 'react-redux'
import * as R from "ramda";

import {
    getTotalBasketPrice,
    getBasketPhonesWithCount
    
} from 'selectors'

import {
    removePhoneFromBasket,
    cleanBasket,
    basketCheckout
} from 'actions';

const Basket = ({
    phones,
    totalPrice,
    removePhoneFromBasket,
    cleanBasket,
    basketCheckout
}) => {
    const isBasketEmpty = R.isEmpty(phones);
    const renderContent = () => (            
        <div>
            {isBasketEmpty && <div>Ваша корзина пуста</div>}
            <div className='table-responsive'>
                <table className='table-bordered table-striped table-condensed cf'>
                    <tbody>
                        {phones.map((phone, index) => (                            
                            
                            <tr className='item-checkout' key={index}>                                  
                                <dt className='first-column-checkout'>
                                    <img
                                        className='img-thumbnail'
                                        src={phone.image}
                                        alt={phone.name}
                                    ></img>
                                </dt>
                                <td>{phone.name}</td>
                                <td>${phone.price}</td>
                                <td>{phone.count}</td>
                                <td>
                                    <span
                                        className='delete-cart'
                                        onClick={()=> removePhoneFromBasket(phone.id)}
                                    >

                                    </span>
                                </td>
                            </tr>
                        ) )}
                    </tbody>
                </table>
            </div>
            {R.not(isBasketEmpty) && (
                <div className='row'>
                    <div className='pull-right total-user-checkout'>
                        <b>Total:</b> ${totalPrice}                        
                    </div>
                </div>
            )}
        </div>
        )
      
    const renderSidebar = () => {
        return (
            <div>
                <Link
                    to='/'
                    className='btn btn-info'
                >
                    <span className='glyphicon glyphicon-info-sign' /> Continue shoping
                </Link>
                {R.not(isBasketEmpty) && 
                    <div>
                        <button
                            className='btn btn-danger'
                            onClick={()=> cleanBasket()}
                        >
                            <span className='glyphicon glyphicon-trash' /> Clear cart
                        </button>
                        <button
                            className='btn btn-success'
                            onClick={()=> basketCheckout(phones)}
                        >
                            <span className='glyphicon glyphicon-envelope' /> Checkout

                        </button>
                    </div>
                }
            </div>
        )
    }  
return (
    <div className='view-container'>
        <div className='container'>
              <div className='row'>
                  <div className='col-md-9'>{ renderContent() }</div>
                  <div className='col-md-3 btn-user-checkout'>{ renderSidebar() }</div>
              </div>       
        </div>
    </div>
  )
}
const mapStateToProps = state => {
    return {
        phones: getBasketPhonesWithCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
}
const mapDispatchToProps = {
    removePhoneFromBasket,
    cleanBasket,
    basketCheckout
}
export default connect(mapStateToProps,mapDispatchToProps) (Basket)
