import React from 'react'
import { connect } from 'react-redux'
import * as R from "ramda";

import {getTotalBasketPrice, getBasketPhonesWithCount} from 'selectors'

const Basket = ({ phones, totalPrice }) => {
    const isBasketEmpty = R.isEmpty(phones);
    const renderContent = () => (            
        <div>
            {isBasketEmpty && <div>Ваша корзина пуста</div>}
            <div className='table-responsive'>
                <table className='table-bordered table-striped table-condensed cf'>
                    <tbody>
                        {phones.map((phone, index) => (                            
                            
                            <tr className='item-checkout' key={index}>                                  
                                <dt className='first-column-checkout'>
                                    <img
                                        className='img-thumbnail'
                                        src={phone.image}
                                        alt={phone.name}
                                    ></img>
                                </dt>
                                <td>{phone.name}</td>
                                <td>${phone.price}</td>
                                <td>{phone.count}</td>
                                <td className='delete-cart'></td>
                            </tr>
                        ) )}
                    </tbody>
                </table>
            </div>
            {R.not(isBasketEmpty) && (
                <div className='row'>
                    <div className='pull-right total-user-checkout'>
                        <b>Total:</b> ${totalPrice}                        
                    </div>
                </div>
            )}
        </div>
        )
      
    const renderSidebar = () => {
        return (
            <div>Sidebar</div>
        )
    }  
return (
    <div className='view-container'>
        <div className='container'>
              <div className='row'>
                  <div className='col-md-9'>{ renderContent() }</div>
                  <div className='col-md-3 btn-user-checkout'>{ renderSidebar() }</div>
              </div>       
        </div>
    </div>
  )
}
const mapStateToProps = state => {
    return {
        phones: getBasketPhonesWithCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
}
export default connect(mapStateToProps,null) (Basket)
