import * as R from "ramda";

import { FETCH_PHONE_BY_ID_SUCCESS } from "actionTypes";

const initialSate = {
    id: null
};

export default (state = initialSate, { type, payload }) => {
    switch (type) {
        case FETCH_PHONE_BY_ID_SUCCESS:
            return R.merge(state, {
                id: R.prop("id", payload)
            });
        default:
            return state;
    }
};
