import * as R from "ramda";

import { FETCH_PHONES_SUCCESS } from "actionTypes";

const initialSate = {
    ids: []
}

export default (state = initialSate, { type, payload }) => { 
    switch (type) {
        case FETCH_PHONES_SUCCESS:
            return R.merge(state, {
                ids: R.pluck('id', payload)
            });
        default:
            return state;
    }
}
